class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        pref = [0] * (k + 1)
        for i in range(n // 2):
            x, y = nums[i], nums[n - i -1]

            max_ = max([abs(k - x), abs(k - y), x, y])
            if max_ < k:
                pref[k] += 2
                pref[max_] -= 1
            else:
                pref[k] += 1

            a = abs(x - y)
            pref[a] -= 1
            if a - 1 >= 0:
                pref[a - 1] += 1

        curr = 0
        res = n // 2
        for i in range(k, -1, -1):
            curr += pref[i]
            res = min(res, curr)

        return res
