class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
          map<int,vector<int>> mp;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            int x = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(x-b);
            int A = max({x,b,k-x,k-b});
            mp[diff].push_back(A);
            v.push_back(A);
        }
        int result = nums.size();
        sort(v.begin(),v.end());

        for(auto &it:mp){
            int diff = it.first;
            int t = lower_bound(v.begin(),v.end(),diff)-v.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &y:it.second){
                if(y<diff){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            result = min(result,t);

        }

        return result;
    }
};
