class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        int n=nums.size();
        vector<int> diff, maxchl, maxchu;
        for(int i=0; i<n/2; i++){
            mp[abs(nums[i]-nums[n-1-i])]++;
            diff.push_back(abs(nums[i]-nums[n-1-i]));
            int a=min(nums[i],nums[n-1-i]);
            int b=max(nums[i],nums[n-1-i]);
            maxchu.push_back(max(k-b,a));
            maxchl.push_back(max(b,k-a));
        }
        // sort(v.rbegin(),v.rend());
        vector<pair<int,int>> mpv;
        for(auto x:mp) mpv.push_back({x.second,x.first});
        auto cmp=[&](pair<int,int>& a, pair<int,int>& b){
            if(a.first==b.first) return a.second>b.second;
            return a.first<b.first;
        };
        sort(mpv.begin(),mpv.end(),cmp);
        reverse(mpv.begin(),mpv.end());
        vector<int> mpvv;
        for(auto x:mpv) mpvv.push_back(x.second);
        int l=0;
        int ans=INT_MAX;
        auto func=[&](int d){
            int ans=0;
            for(int i=0; i<diff.size(); i++){
                if(diff[i]==d) continue;
                int ch=d-diff[i];
                ans++;
                if(ch>0 && maxchu[i]<ch) ans++;
                if(ch<0 && maxchl[i]<abs(ch)) ans++;
            }
            return ans;
        };
        for(int i=0; i<10; i++){
            if(l==mpvv.size()) break;
            ans=min(ans,func(mpvv[l]));
            l++;
        }
        return ans;
    }
};
