class Solution {
public:

    vector<int>nums;
    int n;
    vector<int>f;

    map<int,vector<int>>op;
    int minChanges(vector<int>& nums, int k) 
    {
        this->nums=nums;
         n=nums.size();
        int b=n/2;

        vector<int>temp=nums;

        sort(temp.begin(), temp.end());
        int sol=temp.size();       
        vector<int>g=temp;
        sort(g.begin(), g.end());
        int cc=0;

        for(int i=0;i<n/2;i++)
        {
            int r=max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            cc=abs(nums[i]-nums[n-i-1]);
                       f.push_back(r);
                       g.push_back(r);                       
                       op[cc].push_back(r);        
        }

                sort(f.begin(),f.end());



                      for(auto i:op)
                      {
                          int yy=i.first;
                          vector<int>y=i.second;            
            int ab = lower_bound(f.begin(),f.end(),yy)-f.begin();
            ab = 2*ab+(b-ab);

            for(auto &gg:y)
            {
                if(gg>=yy)
                {
                    ab-=1;
                }
                else
                {
                    ab-=2;
                }
            }

            sol= min(sol,ab);
                      }

return sol;
    }
};
