bool sortbysec(const pair<int, int>&i, const pair<int, int>&j) { 
    return i.second > j.second; 
}
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> m;
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
        }
        vector<pair<int,int>> diff;
        for(auto it:m){
            diff.push_back(it);
        }
        sort(diff.begin(),diff.end(),sortbysec);
        int mn=INT_MAX;
        for(int d=0;d<diff.size();d++){
            int ans=0,X=diff[d].first,t=0;
            bool flag=true;
            for(int i=0;i<n/2;i++){
                if(abs(nums[i]-nums[n-i-1])!=diff[d].first){
                    if(nums[i]+X<=k && nums[i]+X>=0){
                        ans++;
                    }
                    else if(nums[i]-X<=k && nums[i]-X>=0){
                        ans++;
                    }
                    else if(nums[n-i-1]+X<=k && nums[n-i-1]+X>=0){
                        ans++;
                    }
                    else if(nums[n-i-1]-X>=0 && nums[n-i-1]-X<=k){
                        ans++;
                    }
                    else{
                        ans+=2;
                        t++;
                    }
                }
            }
            mn=min(mn,ans);
            if(d<diff.size()-1 && diff[d].second-diff[d+1].second>=t){
                break;
            }
        }
        return mn;
    }
};
