class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int[] dp = new int[k + 2];
        dp[0] = n;
        for (int i = 0; i < n / 2; i++) {
            dp[Math.abs(nums[i] - nums[n - 1- i])]--;
            dp[Math.abs(nums[i] - nums[n - 1- i]) + 1]++;
            int max = Math.max(Math.max(nums[i], k - nums[i]), Math.max(nums[n - 1 -i], k - nums[n - 1 -i]));
            dp[0]--;
            dp[max + 1]++;
        }
        int res = dp[0];
        for (int i = 1; i <= k; i++) {
            dp[i] += dp[i - 1];
            res = Math.min(res, dp[i]);
        }
        return res;
    }
}
