class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp,mx;
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-1-i])]++;
            int x = max(max(nums[i],nums[n-1-i]),k - min(nums[i],nums[n-1-i]));
            mx[x]++;
        }
        int ans = n;
        n/=2;
        for(int i = 0,c=0;i<=k;i++){
            ans=min(n+c-mp[i],ans);
            c+=mx[i];
        }
        return ans;
    }
};
