class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n=nums.size();

        vector<int>v(k+2,0);
        map<int,int>mp;

        for(int i=0;i<n/2;i++){

            mp[abs(nums[i]-nums[n-i-1])]++;


        }


        for(int i=0;i<n/2;i++){


            int d1=max(nums[i],nums[n-i-1]);
            int d2=k-min(nums[i],nums[n-i-1]);

            int d=max(d1,d2);

            v[0]++;
            v[d+1]--;


        }

        for(int i=1;i<v.size();i++){
            v[i]+=v[i-1];

        }
        int ans=INT_MAX;

        for(int i=0;i<v.size();i++){

            int tot=n/2;
            int one=v[i]-mp[i];
            int two=n/2-v[i];

            ans=min(ans,one+two*2);

        }
        return ans;










    }
};
