class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> diff(n/2);
        map<int,int> cnt;
        for(int i=0 ; i<n/2 ; i++){
            cnt[abs(nums[i]-nums[n-i-1])]++;
            int mx=max(nums[i],nums[n-i-1]);
            int mn=min(nums[i],nums[n-i-1]);
            diff[i]=max(mx,k-mn);
        }
        sort(begin(diff),end(diff));
        for(auto x:diff){
            cout<<x<<endl;
        }
        int ans=4*n;
        for(int x=0 ; x<=k ; x++){
            int e=lower_bound(begin(diff),end(diff),x)-begin(diff);
            //cout<<x<<" "<<e<<endl;
            ans=min(ans,e*2+(n/2-e)-cnt[x]);
        }
        return ans;
    }
};
