class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int len=n/2;
        unordered_map<int,int> mp;
        vector<int> cnt(k+1);
        for(int i=0;i<n/2;++i){
            mp[abs(nums[i]-nums[n-i-1])]++;
            int x=max(max(nums[i],nums[n-i-1]), max(k-nums[i],k-nums[n-i-1]));
            cnt[x]++;
        }
        int ans=n,res,sum=0;
        for(int i=0;i<=k;++i){
            res=sum*2+(n/2-sum)-mp[i];
            ans=min(ans,res);
            sum+=cnt[i];
        }
        return ans;
    }
};
