class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        map<int,int>m;
        vector<int>x;
        for(int i=0;i<n/2;i++){
            m[abs(a[i]-a[n-i-1])]++;
            x.push_back( max( max(abs(a[i]),abs(k-a[i])),max(abs(a[n-i-1]),abs(k-a[n-i-1])) ) );
        }
        sort(x.begin(),x.end());

        int ans=INT_MAX;
        for(auto it=m.begin();it!=m.end();it++){
            auto ip=lower_bound(x.begin(),x.end(),it->first)-x.begin();
            int s=ip + n/2 - it->second ;
            ans=min(ans,s);
        }
        return ans;

    }
};
