class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        int n=arr.size();
        // int ans=INT_MAX;
        vector<int>pf(k+1,0);
        map<int,int>mp;
        for(int i=0;i<(n/2);i++){
            int a=min(arr[n-1-i],arr[i]);
            int b=max(arr[i],arr[n-i-1]);
            mp[abs(a-b)]+=1;
            int z=k-a;
            int x=b;
            int y=max(x,z)+1;
            if(y<=k){
                pf[y]+=2;
            }
        }
        for(int i=0;i<=k;i++){
            if(i-1>=0){
                pf[i]+=pf[i-1];
            }
        }
        int ans=INT_MAX;
        for(int i=0;i<=k;i++){
            int two=pf[i];
            int one=(n/2)-(two/2);
            int rand=0;

            if(mp.find(i)!=mp.end()){
                rand+=mp[i];    
            }
            int val=two+one-rand;
            // cout<<"k "<<i<<" val "<<val<<" two "<<two<<" on e "<<one<<" mp[i] "<<rand<<"\n"; 
            ans=min(ans,val);
            // cout<<"ans "<<ans<<"\n";
        }
        return ans;
    }
};
