class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mx;
        vector<int> ve;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            //later see with dp too
            int X = max({a,b,k-a,k-b});
            mx[diff].push_back(X);
            ve.push_back(X);
        }
        int fin = nums.size();
        sort(ve.begin(),ve.end());
        for(auto &val:mx){
            int df = val.first;
            int T = lower_bound(ve.begin(),ve.end(),df)-ve.begin();
            T = T*2+(nums.size()/2-T);
            for(auto &X:val.second){
                if(X<df){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }
            fin = min(fin,T);
        }
        return fin;
    }
};
