class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();

        int m=1e6,minVal=INT_MAX;
        vector<int>count(m,0);

        for(int i=0;i<n/2;i++){
            int num1=nums[i],num2=nums[n-i-1];
            int maxNum=max(num1,num2);
            int minNum=min(num1,num2);

            int range=max(k-minNum,maxNum);

            count[0]+=1;
            count[maxNum-minNum]-=1;
            count[maxNum-minNum+1]+=1;
            count[range+1]+=1;
        }

        int res=0;

        for(int i=0;i<m;i++){
            res+=count[i];
            minVal=min(minVal,res);
        }
        return minVal;
    }
};
