class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> dMap;
        vector<int> vList;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int l = nums[i];
            int r = nums[nums.size() - i - 1];
            int d = abs(l - r);
            int x = max({l, r, k - l, k - r});

            dMap[d].push_back(x);
            vList.push_back(x);
        }

        int ans = nums.size();
        sort(vList.begin(), vList.end());

        for (const auto& [d, vals] : dMap) {
            int idx = lower_bound(vList.begin(), vList.end(), d) - vList.begin();
            int ch = idx * 2 + (nums.size() / 2 - idx);

            for (const auto& x : vals) {
                if (x < d) {
                    ch -= 2;
                } else {
                    ch -= 1;
                }
            }

            ans = min(ans, ch);
        }

        return ans;
    }
};
