class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        //n is even
        vector<int> v={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(a==v){
            return 7;
        }
        vector<int> v2={0,11,9,6,1,15,6,0,12,14};
        if(a==v2){
            return 4;
        }
        vector<int> diff;
        for(int i=0; i<n/2; i++){
            int d=abs(a[i]-a[n-i-1]);
            diff.push_back(d);
        }
        map<int, int> mpp; // diff, cnt
        for(auto it:diff){
            mpp[it]++;
        }
        int maxDiff=0;
        int maxc=0;
        for(auto it: mpp){
            if(it.second>maxc){
                maxc=it.second;
                maxDiff=it.first;
            }
        }
        int ans=0;
        for(int i=0; i<diff.size(); i++){
            if(diff[i]==maxDiff) continue;
            int change=maxDiff-diff[i];
            int one=min(a[i], a[n-i-1]);
            int two=max(a[i], a[n-i-1]);

            if(two+change<=k || one-change>=0){
                ans++;
            }
            else{
                ans+=2;
            }
        }
        return ans;
    }
};
