class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = INT_MAX;

        int limit=1e5+10;

        vector<int> count(limit,0),val(limit,0);
        for(int i=0,j=n-1;i<n/2;i++,j--){
            int maxi = max({abs(nums[i] - k), abs(nums[j] - k), abs(nums[i]-0), abs(nums[j]-0)});
            maxi = max(maxi,nums[i]);
            maxi = max(maxi,nums[j]);
            count[0]++;
            count[maxi+1]--;

            val[abs(nums[i]-nums[j])]++;
        }


        for(int i=1;i<limit;i++){
            count[i] += count[i-1];
        }

        n = n/2;
        for(int i=0;i<limit;i++){
            // cout<<count[i]<<" "<<val[i]<<endl;
            int curr = 2*(n-count[i])+count[i]-val[i];
            // cout<<"curr : "<<curr<<endl;
            ans = min(ans,curr);
        }


        return ans;
    }
};
