class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;
        vector<int>v;
        int n=nums.size();
        int res=nums.size();
        for(int i=0;i<(nums.size()+1)/2;i++){
            mp[abs(nums[i]-nums[nums.size()-i-1])].push_back(max({nums[i],nums[nums.size()-i-1],k-nums[i],k-nums[nums.size()-1-i]}));
            v.push_back(max({nums[i],nums[nums.size()-i-1],k-nums[i],k-nums[nums.size()-1-i]}));
        }
        sort(v.begin(),v.end());
        for(auto it:mp){
            int x = lower_bound(v.begin(),v.end(),it.first)-v.begin();
            x = x*2+((n/2)-x);
            for(auto it1:it.second){
                if(it1<it.first)x-=2;
                else x-=1;
            }
            res=min(res,x);
        }
        return res;
    }
};
