class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),m=n/2;
        vector<int>p(k+2);
        for(int i=0;i<m;i++){
            int a=max(nums[i],nums[n-i-1]),b=min(nums[i],nums[n-i-1]);
            int d=a-b,r=max(b,k-a);
            p[d+1]++;
            p[d+r+1]--;
            p[0]++;
            p[d]--;
            p[d+r+1]+=2;
            p[k+1]-=2;
        }
        int res=p[0];
        for(int i=0;i<k;i++){
            p[i+1]+=p[i];
            res=min(res,p[i+1]);
        }
        return res;
    }
};
