#include <vector>
#include <map>
#include <algorithm>
#include <cmath>
#include <limits>

class Solution {
public:
    int minChanges(std::vector<int>& n, int kl) {
        std::map<int, std::vector<int>> m;
        std::vector<int> v;

        for (int i = 0; i < n.size() / 2; ++i) {
            int x = n[i];
            int y = n[n.size() - i - 1];
            int diff = std::abs(x - y);

             int X = std::max({x, y, kl - x, kl - y});

             m[diff].push_back(X);
            v.push_back(X);
        }

        int ans = n.size();  
        std::sort(v.begin(), v.end());

        for (const auto& [diff, values] : m) {
            int T = std::lower_bound(v.begin(), v.end(), diff) - v.begin();
            T = T * 2 + (n.size() / 2 - T);

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = std::min(ans, T);
        }

        return ans;
    }
};
