class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        map<int,int>m;
        for(int i=0; i<n/2; i++){
            m[abs(a[i]-a[n-1-i])]++;
        }
        int ans=INT_MAX,x;
        int ma=INT_MIN;
        for(auto e:m){
            ma=max(e.second,ma);
        }
        for(auto e:m){
            if(e.second!=ma && e.second!=(ma-1)){
                m.erase(e.first);
            }
        }
        for(auto e:m){
            x=e.first;
            int temp=0;
            for(int i=0; i<n/2; i++){
                if(abs(a[i]-a[n-1-i])!=x){
                    int p=max(a[i],a[n-i-1]);
                    int q=min(a[i],a[n-i-1]);
                    if(p+x<=k || p-x>=0 || q+x<=k || q-x>=0){
                        temp++;
                    }else{
                        temp+=2;
                    }
                }
            }
            ans=min(ans,temp);
        }
        return ans;
    }
};
