class Solution {
public:

    void add(vector<int> & v, int start, int end, int val) {
        if (start >= v.size()) return;
        v[start] += val;
        if (end+1 >= v.size()) return;
        v[end+1] -= val;
    }

    void trans(vector<int> & v) {
        for (int i = 1; i < v.size(); i++) {
            v[i] += v[i-1];
        }
    }

    void print(vector<int> & v) {
        for (int x: v)
            cout << x << ' ';
        cout << endl;
    }

    int minChanges(vector<int>& nums, int k) {
        vector<int> v;
        int n = nums.size();
        v = vector<int>(k+1, 0);
        for (int i = 0; i < n/2; i++) {
            int x = min({nums[i], k - nums[i], nums[n-i-1], k - nums[n-i-1]});
            int y = abs(nums[i] - nums[n-i-1]);
            add(v, 0, k-x, 1);
            add(v, y, y, -1);
            add(v, k-x+1, k, 2);
            // print(v);
        }

        int ans = INT_MAX;
        trans(v);
        // print(v);
        for (int x : v) {
            ans = min(ans, x);
        }

        return ans;
    }
};
