class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n = nums.size() ;
        map<int,int> cnt ;
        vector<int> maxi(n/2) ;

        for( int i=0 ; i<n/2 ; i++ ){
            int curr = abs( nums[i] - nums[n-i-1]) ;
            cnt[curr]++ ;

            int temp = abs( nums[i] - nums[n-i-1]) + max( k-max(nums[i], nums[n-i-1]) , min( nums[i] , nums[n-i-1]) ) ;
            // cout << i << "  " << cnt[curr] << "  " << max( k-max(nums[i], nums[n-i-1]) , min( nums[i] , nums[n-i-1]) ) << endl ;
            maxi[i] = temp ;
        }

        // for( auto i : maxi ) cout << i << " " ;
        // cout << endl ;

        sort( maxi.begin() , maxi.end() ) ;

        // for( auto i : maxi ) cout << i<< " " ;
        // cout << endl ;

        int ans = n/2 ;
        for( int i=0 ; i<=k ; i++ ){

            auto it = lower_bound( maxi.begin() , maxi.end() , i ) ;
            int a = it - maxi.begin() ;
            int b = maxi.end() - it ;

            int tempAns = (a*2) + (b) - (cnt[i]) ;
            ans = min( ans , tempAns ) ;

            // cout << i << " " << a << " " << b << " " << cnt[i] << " -> " << ans << endl ;            

        }

        return ans ;

    }
};
