#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
int dp[100001];
    int minChanges(vector<int>& nums, int k) {
            vector<int> v;
            int mino =1000000000;
        for(int i=0;i<nums.size()/2;i++)
        {
            dp[abs(nums[i]-nums[nums.size()-i-1])]++;
            int val1= max(nums[i],k-nums[i]);
            int val2= max(nums[nums.size()-i-1],k-nums[nums.size()-i-1]);
            v.push_back(max(val1,val2));
            // cout<<v[i]<<" ";
        }
        sort(v.begin(),v.end());
        int n=v.size();
        for(int i=0;i<=k;i++)
        {
            int use= lower_bound(v.begin(),v.end(),i)-v.begin();
            if(use==v.size())
            {
                // cout<<2*n<<endl;
                // cout<<"ok"<<endl;
            mino= min(mino,(n*2));
            }
            else
            {
                use--;
                int ok=(2*(use+1)+(v.size()-use-1)-dp[i]);
                // cout<<ok<<endl;
                mino= min(mino,ok);
            }
            // cout<<mino<<endl;
        }
        return mino;

    }
};
