class Solution {
public:
    void check(vector<int>& nums, int val,int k, int& ans){
        int curr = 0, n = nums.size();
        for(int i = 0;i<n/2; i++){
            if(abs(nums[i] - nums[n-i-1]) != val){
                curr++;
                if((nums[i] - val < 0 || nums[i] - val > k) && (nums[i] + val < 0 || nums[i] + val > k) &&
                (nums[n-i-1] - val < 0 || nums[n-1-i] - val > k) && (nums[n-i-1] + val < 0 || nums[n-i-1] + val > k)){
                    curr++;
                }
            }
        }
        ans = min(ans, curr);
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        map<int,int> mp;
        for(int i = 0;i<n/2;i++){
            int x = abs(nums[i] - nums[n-i-1]);
            mp[x]++;
        }

        vector<pair<int,int>> vect;
        for(auto i : mp){
            vect.push_back({i.second,i.first});
        }
        sort(vect.begin(),vect.end());
        reverse(vect.begin(),vect.end());

        int ans = 1e9;
        for(int i = 0;i<min(10, int(vect.size())); i++){
            check(nums, vect[i].second, k, ans);
        }
        return ans;
    }
};
