class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v(k+1, 0);
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            v[abs(nums[i] - nums[n-i-1])]++;
        }
        int ans = n, m=0;
        for(int j=0;j<=k;j++){
            if(v[j] > m){
                int c=0;
                for(int i=0;i<n/2;i++){
                    int a = max(nums[i], nums[n-i-1]), b=min(nums[i], nums[n-i-1]);
                    if(abs(a - b) == j) continue;
                    else if(a-0>=j || k-b>=j){
                        c += 1;
                    }
                    else{
                        c += 2;
                    }
                }
                if(c<ans){
                    ans = c;
                }
                m = v[j];
            }
        }
        return ans;
    }
};
