class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m;
        vector<int> v;
        for (int i = 0; i < nums.size() / 2; i++) {
            int a = nums[i], b = nums[nums.size() - i - 1];
            int diff = abs(a - b);
            auto getMaxX = [](int a, int b, int k) { 
                return max({a, b, k - a, k - b}); 
            };
            int X = getMaxX(a, b, k);
            m[diff].push_back(X);
            v.push_back(X);
        }
        int ans = nums.size();
        sort(v.begin(), v.end());

        for (auto &val : m) {
            int diff = val.first;
            int pos = lower_bound(v.begin(), v.end(), diff) - v.begin();
            pos = pos * 2 + (nums.size() / 2 - pos);

            auto adjustpos = [&pos, diff](int X) {
                if (X < diff) {
                    pos -= 2;
                } else {
                    pos -= 1;
                }
            };

            for (auto &X : val.second) {
                adjustpos(X);
            }

            ans = min(ans, pos);
        }

        return ans;
    }
};
