class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>v;
        map<int, vector<int>> mpp;
        for(int i = 0;i<nums.size()/2;i++){
            int left = nums[i];
            int right = nums[nums.size() - i - 1];
            int diff = abs(left - right);
            int X = max({left, right, k-left, k-right});
            mpp[diff].emplace_back(X);
            v.emplace_back(X);
        }
        int solution = nums.size();
        sort(v.begin(), v.end());
        for(auto &m:mpp){
            int c = m.first;
            int val = lower_bound(v.begin(), v.end(), c) - v.begin();
            val = val*2 + (nums.size()/2 - val);
            for(auto &s:m.second){
                if(s<c){
                    val -= 2;
                }
                else{
                    val -= 1;
                }
            }
            solution = min(solution, val);
        }
        return solution;
    }
};
