class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)

        ans = n
        cnt = Counter()
        bad_pairs = Counter()
        for i in range(n // 2):
            cur = abs(nums[i] - nums[n - i - 1])
            bad = max(k - min(nums[i], nums[n - i - 1]), max(nums[i], nums[n - i - 1])) + 1
            cnt[cur] += 1
            bad_pairs[bad] += 1
        for i in range(1, k + 1):
            bad_pairs[i] += bad_pairs[i - 1]

        for k, v in cnt.items():
            ans = min(ans, n // 2 - v + bad_pairs[k])

        return ans
