class Solution {
public:

void computeDiffsAndVals(vector<int>& nums, int k, map<int, vector<int>>& m, vector<int>& v) {
    int n = nums.size();
    for (int i = 0; i < n / 2; ++i) {
        int a = nums[i], b = nums[n - i - 1];
        int d = abs(a - b);
        int x = max({a, b, k - a, k - b});
        m[d].push_back(x);
        v.push_back(x);
    }
}


int computeMinChgs( vector<int>& v, map<int, vector<int>>& m, int n) {
    int ans = n;
    vector<int> sv = v;
    sort(sv.begin(), sv.end());

    for (auto& val : m) {
        int d = val.first;
        auto t = lower_bound(sv.begin(), sv.end(), d);
        int it = t - sv.begin();
        it = it * 2 + (n / 2 - it);

        for (auto& x : val.second) {
            (x < d) ? it -= 2 : it -= 1;
        }

        ans = min(ans, it);
    }

    return ans;
}


int minChanges(vector<int>& nums, int k) {
    map<int, vector<int>> m;
    vector<int> v;
    computeDiffsAndVals(nums, k, m, v);
    return computeMinChgs(v, m, nums.size());
}
};
