class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> fr;
        int n = nums.size();
        for (int i = 0; i < n / 2; i++) {
            int a = nums[i], b = nums[n - i - 1];
            int dif = abs(a - b);
            int maxfr = max({a, b, k - a, k - b});
            mp[dif].push_back(maxfr);
            fr.push_back(maxfr);
        }
        int ans = n;
        sort(fr.begin(), fr.end());

        for (auto& [dif, vals] : mp) {
            int chgs = lower_bound(fr.begin(), fr.end(), dif) - fr.begin();
            chgs = chgs * 2 + (n / 2 - chgs);

            for (auto& maxfr : vals) {
                if (maxfr < dif) {
                    chgs -= 2;
                } else {
                    chgs -= 1;
                }
            }

            ans = min(ans, chgs);
        }

        return ans;
    }
};
