class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;

        vector<int>vec;

        for(int i=0; i<nums.size()/2;i++){
            int x = nums[i],y = nums[nums.size()-i-1];
            int diff = abs(x-y);
            int X = max({x,y,k-x,k-y});

            mp[diff].push_back(X);
            vec.push_back(X);
        }

        int ans = nums.size();
        sort(vec.begin(),vec.end());

        for(auto &val:mp){
            int diff = val.first;
            int J= lower_bound(vec.begin(),vec.end(),diff)- vec.begin();

            J = J*2+(nums.size()/2-J);

            for(auto &X:val.second){
                if(X<diff){
                    J -=2;
                }
                else{
                    J-=1;
                }
            }
            ans = min(ans,J);
        }
        return ans;
    }
};
