class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v(k+1,0);
        int n=nums.size();
        for(int i=0;i<n/2;i++)
        {
            int a=max({abs(k-nums[n-i-1]), abs(k-nums[i]), nums[i], nums[n-i-1]});
            int b=abs(nums[n-1-i]-nums[i]);
            v[0]+=1;
            v[b]-=1;
            if(b+1<=k)
            v[b+1]+=1;

            if(a+1<=k)
            {
            v[a+1]-=1;
            v[a+1]+=2;
            }
        }
        int ans=INT_MAX;
        for(int i=1;i<v.size();i++)
        {
            v[i]+=v[i-1];
            ans=min(ans,v[i]);
            // cout<<v[i]<<" ";
        }
        ans=min(v[0],ans);
        return ans;
    }
};
