class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),zr=0;
        vector<int>diff(1e6);
        for(int i=0;i<n/2;i++)
        {
            int mn=min(nums[i],nums[n-i-1]);
            int mx=max(nums[i],nums[n-i-1]);
            int d=mx-mn;
            diff[d+1]++;
            diff[max(mx,k-mn)+1]++;
            diff[max(k,mx)+1]-=2;
            if(d-1>=0)
            {
                diff[0]++;
                diff[d]--;
            }
        }
        int ans=diff[0];
        for(int i=1;i<=k;i++)
        {
            diff[i]+=diff[i-1];
            ans=min(ans,diff[i]);
        }
        return ans;
    }
};
