public class Solution 
{
    public int MinChanges(int[] nums, int k) 
    {
        Dictionary<int,int> dict = new();
        int[] extraChanges = new int[k+1];
        int[] noChanges = new int[k+1];
        for(int i = 0; i < nums.Length/2; i++)
        {
            int temp = Math.Abs(nums[i] - nums[nums.Length-i-1]);
            noChanges[temp]++;
            int min = Math.Min(nums[i],nums[nums.Length-i-1]);
            int max = Math.Max(nums[i],nums[nums.Length-i-1]);
            if(Math.Max(max, k - min) == k)
                continue;
            extraChanges[Math.Max(max, k - min)+1]++;
        }
        for(int i = 1; i <= k; i++)
            extraChanges[i] += extraChanges[i-1];
        Console.WriteLine(String.Join(",",extraChanges));
        Console.WriteLine(String.Join(",",noChanges));

        int count = Int32.MaxValue;
        for(int i = 0; i <= k; i++)
        {
            count = Math.Min(count, nums.Length/2 - noChanges[i] + extraChanges[i]);
        }

        return count;
    }
}
