class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> freq(k+1,0) ;
        vector<int> crit(k+1,0) ; //临界
        vector<int> pre(k+1,0) ; 
        int n = nums.size() ;
        for (int i=0 ; i<n/2 ; ++i) {
            int d = abs(nums[i] - nums[n-1-i]) ;
            int cr = min({nums[i],k-nums[i],nums[n-1-i],k-nums[n-1-i]}) ;
            crit[cr]++ ;
            freq[d]++ ;
        }
        for (int i=0 ; i<=k ; ++i) {
            pre[i] = crit[i] ;
            if (i>0)
            pre[i] += pre[i-1] ;
        }
        int ans = INT_MAX ;
        for (int i=0 ; i<=k ; ++i) {
            ans = min(ans , n/2-freq[i]+n/2-pre[k-i]) ;
        }
        return ans ;
    }
};
