class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> v;
        int n=nums.size();

        for(int i=0;i<n/2;i++) {
            int a=nums[i],b=nums[n-1-i];
            int maxx=max({a,b,k-a,k-b});
            mpp[abs(a-b)].push_back(maxx);
            v.push_back(maxx);
        }
        int res=n;
        sort(v.begin(),v.end());
        for(auto it:mpp) {
            int diff=it.first;
            int t=lower_bound(v.begin(),v.end(),diff)-v.begin();
            t = t*2+(n/2-t);

            for(auto x:it.second) {
                if(x<diff) t-=2;
                else t-=1;
            }
            res=min(res,t);
        }
        return res;
    }
};
