class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> freq(k+1);
        vector<int> pre(k+2);
        int n=nums.size();

        for(int i=0;i<n/2;i++){
            int val=abs(nums[i]-nums[n-i-1]);
            freq[val]++;
            int x=max(nums[i],nums[n-i-1])-0;
            int y=k-min(nums[i],nums[n-i-1]);
            pre[0]++;
            pre[max(x,y)+1]--;
        }


        for(int i=1;i<=k;i++){
            pre[i]+=pre[i-1];
        }

        for(int i=0;i<=k;i++){
            pre[i]=n/2-pre[i];
        }

        int mini=INT_MAX;

//         for(int i=0;i<=k;i++){
//             cout<<freq[i]<<" ";
//         }
//         cout<<endl;

//         for(int i=0;i<=k;i++){
//             cout<<pre[i]<<" ";
//         }
//         cout<<endl;



        for(int i=0;i<=k;i++){
            int val=pre[i]*2+(n/2-pre[i]-freq[i]);
            // cout<<i<<" "<<val<<endl;
            mini=min(mini,val);
        }

        return mini;

    }
};
