class Solution {
public:
    int howMany(int num,vector<int>&arr){
        if(arr[0]>=num) return 0;
        int res=-1,s=0,e=arr.size()-1;
        while(s<=e){
            int mid=s+(e-s)/2;
            if(arr[mid]<num){
                res=mid;
                s=mid+1;
            }
            else{
                e=mid-1;
            }
        }
        return res+1;
    }
    int minChanges(vector<int>& nums, int k) {
        vector<int> maxis;
        for(int i=0;i<nums.size()/2;i++){
            int num1=max(nums[i],nums[nums.size()-1-i]);
            int num2=min(nums[i],nums[nums.size()-1-i]);
            int num=max(num1,k-num2);
            maxis.push_back(num);
        }
        sort(maxis.begin(),maxis.end());
        unordered_map<int,int> u;
        for(int i=0;i<nums.size()/2;i++){
            int num=abs(nums[i]-nums[nums.size()-1-i]);
            u[num]++;
        }
        int ans=nums.size()/2;
        for(auto i:u){
            int num1=i.second;
            int num2=howMany(i.first,maxis);
            int numb=nums.size()/2;
            ans=min(ans,(int)(numb-num1+num2));
            // cout<<num1<<" "<<num2<<" "<<ans<<endl;
        }
        return ans;
    }
};
