class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> mp;
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-1-i]);
            mp[diff]++;
        }
        int maxdiff=0,maxfreq=0;
        for(auto p:mp){
            if(p.second>maxfreq){
                maxdiff=p.first;
                maxfreq=p.second;
            }
        }
        vector<int>maxdiffs;
        for(auto p:mp){
            if(p.first<=maxdiff){
                maxdiffs.push_back(p.first);
            }
        }
        int mincnt=INT_MAX;
        for(auto maxdiff:maxdiffs){
            int cnt=0;
            for(int i=0;i<n/2;i++){
                int diff=abs(nums[i]-nums[n-1-i]);
                if(diff!=maxdiff){
                    if((maxdiff+min(nums[i],nums[n-1-i])>k) && (max(nums[i],nums[n-1-i])-maxdiff<0)){
                        cnt+=2;
                    }else{
                        cnt++;
                    }
                }
            }
            mincnt=min(mincnt,cnt);
        }

        return mincnt;
    }
};
