class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans=INT_MAX;
        int n=nums.size();
        map<int,int> mp;
        vector<int> diff(k+1,0);
        vector<int> pre(k+1,0);
        for(int i=0;i<n/2;i++)
        { 
            int x=0;
            mp[abs(nums[i]-nums[n-i-1])]++;
            if(nums[i]>nums[n-i-1])
                x=max(k-nums[n-i-1],nums[i]-0);
            else
                x=max(k-nums[i],nums[n-i-1]-0);
            diff[x]++;
        }
        int prefix=0;
        for(int i=0;i<=k;i++)
        {
            prefix+=diff[i];
            pre[i]=prefix;
        }
        for(auto pair:mp)
        {
            int diff=pair.first;
            int count=pair.second;
            if(diff>0)
                ans=min(ans,n/2-count+pre[diff-1]);
            else
                ans=min(ans,n/2-count);
        }
        return ans;
    }
};
