class Solution {
public:
    int bs(vector<int> &v, int diff){
        int l=0,r=v.size()-1,ans=-1;
        while(l<=r){
            int m=(l+r)/2;
            if(diff<=v[m]){
                // if(diff==4) cout<<diff<<" "<<v[m]<<" l=m+1"<<endl;
                r=m-1;
            }else{
                // if(diff==4) cout<<diff<<" "<<v[m]<<" r=m-1"<<endl;
                ans=max(ans,m);
                l=m+1;
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),ans=1e9; map<int,int> mp; vector<int> v;
        int l=0,r=n-1,maxi=0;
        while(l<r){
            int diff=abs(nums[l]-nums[r]);
            mp[diff]++;
            int ldiff=min(nums[l],nums[r])-0; int rdiff=k-max(nums[l],nums[r]);
            int newdiff=diff+max(ldiff,rdiff);
            v.push_back(newdiff);
            l++; r--;
        }
        sort(v.begin(),v.end());

        for(int diff=0;diff<=k;diff++){
            int cnt=bs(v,diff)+1;
            int tans1=cnt;
            int tans2=v.size()-cnt;
            int tans=2*tans1+tans2 - mp[diff];
            // cout<<cnt<<endl;
            ans=min(ans,tans);
        }
        return ans;
    }
};
