class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();

        vector<int> p(k+2);

        // map<int,int> ma;

        for(int i=0; i<n/2; i++) {
            // ma[abs(a[i]-a[n-1-i])]++;
            int mi = min(a[i], a[n-1-i]);
            int mx = max(a[i], a[n-1-i]);
            int d = abs(a[i] - a[n-1-i]);
            int maxAdd = max(mi, k-mx);
            int two = d + maxAdd + 1;

            p[0]+=1;

            p[two] +=1;

            p[d]-=1;
            p[d+1]+=1;

        }

        for(int i=1; i<k+2; i++) {
            p[i] += p[i-1];    
        }

        return *min_element(all(p));

        // int mx = 0;
