#define all(v)  v.begin() , v.end()
#define pb  push_back
class Solution {
public:
    int minChanges(vector <int> &arr, int k) {

        int n = arr.size() , change = 2e9;

        vector < int > v;
        unordered_map < int , vector < int > > m;

        for(int i = 0; i < n / 2; i++){

            int maxi = max(arr[i] , arr[n - i - 1]);
            maxi = max(maxi , max(k - arr[i] , k - arr[n - i - 1]));

            m[abs(arr[i] - arr[n - i - 1])].pb(maxi);
            v.pb(maxi);
        }

        sort(all(v));

        for(auto [x , vec] : m){

            int count = lower_bound(all(v) , x) - v.begin();
            count += n / 2;

            for(auto i : vec){

                if(i >= x)  count--;
                else        count -= 2;
            }

            change = min(change , count);

        }

        return change;

    }
};
