class Solution 
{
public:
    int minChanges(vector<int>& nums, int k) 
    {
        int n = size(nums), sz = n / 2, ans = n;
        vector<int> temp; map<int,vector<int>> mp;

        for(int i = 0; i < sz; i++)
        {
            int f = nums[i], s = nums[n - i - 1], maxi = max({f ,s ,k - f, k - s});
            mp[abs(s - f)].push_back(maxi);
            temp.push_back(maxi);
        } 

        sort(begin(temp),end(temp));
        for(auto &[d, arr] : mp)
        {
            int cur = upper_bound(begin(temp),end(temp), d - 1) - begin(temp), cur2 = sz - cur; cur *= 2;
            for(auto &j : arr) cur -= (j < d ? 2 : 1);
            ans = min(ans, cur + cur2);
        }
        return ans;
    }
};
