class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        diff = [0] * (k + 2)
        for i in range(n // 2):
            v = abs(nums[i] - nums[n - i - 1])
            d = max(max(nums[i], nums[n - i - 1]), k - min(nums[i], nums[n - i - 1]))
            diff[0] += 1
            diff[v] -= 1
            diff[v + 1] += 1
            diff[d + 1] += 1
            diff[k + 1] -= 2
        for i in range(k + 1):
            diff[i + 1] += diff[i]
        diff.pop()
        return min(diff)
