class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int>h;
        for(int i=0;i<n/2;i++) {
            h[abs(nums[i]-nums[n-i-1])]++;
        }
        vector<pair<int,int>>g;
        for(auto &i:h) {
            g.push_back({i.second,i.first});
        }
        sort(g.begin(),g.end());
        int l=g.size();
        int ans=INT_MAX;
        // for(auto &i:g) {
        //     cout << i.second << " " << i.first << "\n";
        // }
        for(int t=l-1;t>=0;t--) {
            int x=g[t].second;
            int res=0;
            for(int i=0;i<n/2;i++) {
                int l=nums[i],r=nums[n-i-1];
                if(l<r) {
                    if(r-l==x) continue;
                    if(r-x>=0 && r-x<=k) res++;
                    else if(x+l>=0 && x+l<=k) res++;
                    else if(x>k) {
                        res=INT_MAX;
                        break;
                    } else res+=2;
                } else {
                    if(l-r==x) continue;
                    if(l-x>=0 && l-x<=k) res++;
                    else if(x+r>=0 && x+r<=k) res++;
                    else if(x>k) {
                        res=INT_MAX;
                        break;
                    } else res+=2;
                }
            }
            if(res==INT_MAX) continue;
            if(ans<res) break;
            ans=min(ans,res);
        }
        return ans;

    }
};
