class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int m = n / 2;
        unordered_map<int, int> diff;
        int lo = 0;
        int hi = n - 1;
        vector< pair<int, int> > sorted;
        while (lo < hi)
        {
            int val = abs(nums[lo] - nums[hi]);            
            diff[val]++;
            ++lo;
            --hi;            
        }

        for (auto& item : diff)
        {
            sorted.push_back( {item.second, item.first} );
        }
        sort(sorted.begin(), sorted.end());
        reverse(sorted.begin(), sorted.end());

        int res = INT_MAX;
        for (int i = 0; i < 10 && i < sorted.size(); ++i)
        {
            int cnt = 0;
            // int target = i;
            int target = sorted[i].second;
           // cout << target << endl;
            for (int lo = 0; lo < m; ++lo)
            {
                int hi = n - lo - 1;
                if ( abs(nums[lo] - nums[hi]) == target )
                {
                    continue;
                }

                if (nums[lo] < nums[hi]) swap(nums[lo], nums[hi]);
                int left = target - (nums[lo] - nums[hi]);

                if (left > 0)
                {
                    if (nums[lo] + left <= k) ++cnt;
                    else if (nums[hi] - left >= 0) ++cnt;
                    else cnt += 2;
                }
                else if (left < 0)
                {
                    if (nums[hi] + left <= k) ++cnt;
                    else if (nums[lo] - left >= 0) ++cnt;
                    else cnt += 2;
                }


                //cout << lo << "," << nums[hi] << "," << left << "|" << cnt << endl;
            }
           // cout << target << "," << cnt << endl;
            res = min(res, cnt);
        }


        return res;
    }
};
