class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int ans=n;
        int temp=0;
        int maxi=INT_MIN;
        vector<int>vec;
        unordered_map<int,vector<int>>mp;

        for(int i=0;i<n/2;i++){
            temp=abs(nums[i]-nums[n-i-1]);
            maxi=max({nums[n-1-i],nums[i],k-nums[i],k-nums[n-i-1]});
            vec.push_back(maxi);
            mp[temp].push_back(maxi);   
        }

        sort(vec.begin(),vec.end());

        for(auto &it:mp){
            int temp1=(lower_bound(vec.begin(),vec.end(),it.first)-vec.begin())+n/2;
            for(auto i:it.second){
                if(i>=it.first){
                    temp1-=1;
                }
                else{
                    temp1-=2;
                }
            }

            ans=min(ans,temp1);

        }

        return ans;


    }
};
