class Solution {

typedef long double lld;
typedef long long ll;
#define fr(a,b,i) for (ll i=a;i<b;i++)
#define rfr(a,b,i) for(ll i=a;i>=b;i--)
#define rep(i,n) fr(0,n,i)
#define ff first
#define ss second
#define pb push_back
#define endl '\n'
#define inf LLONG_MAX
#define all(c) (c).begin(), (c).end()
const ll mod = 1000000007;

template <typename T>
void chmin(T &f, T s){ f = min(f, s); }
template <typename T>
void chmax(T &f, T s){ f = max(f, s); }
ll expo(ll a, ll b, ll mod) {ll res = 1; while (b > 0)
{if (b & 1)res = (res * a) % mod; a = (a * a) % mod; b = b >> 1;} return res;}
ll modinv(ll a, ll b) {return expo(a, b - 2, b);}
ll modadd(ll a, ll b, ll m) { return ( a % m + b % m) % m;}
ll modmul(ll a, ll b, ll m) { return ( a % m * b % m) % m;}
ll modsub(ll a, ll b, ll m) { return ((a%m - b%m)+ m) % m;}
ll moddiv(ll a, ll b, ll m) { return modmul(a, modinv(b, m), m) % m ;}
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size(), ans = n;

        map<int, int> f;
        vector<int> difs;
        rep(i, n/2) difs.pb(abs(a[i] - a[n-1-i]));
        for(auto i: difs) f[i]++;

        sort(all(difs));

        vector<int> b;
        rep(i, n/2){
            int ff = a[i], s  = a[n-i-1];
            if(ff > s) swap(ff, s);

            b.pb(max(s, k - ff));
        }
        sort(all(b));

        for(auto i: difs){
            int temp = 0;
            int l = lower_bound(all(b), i) - b.begin();
            temp += n / 2 + l - f[i];
            chmin(ans, temp);
        }

        return ans;
    }
};
